(ns dragon.maths
  (:refer-clojure)
  (:require [dragon.core :refer [alias!]]))

;;; Mathematical constants ("nullary functions").
(def e* "358 digits of e" 2.)
(def π* "359 digits of π; 360 is the first \"00\" sequence shared with τ" 3.)
(def τ* "359 digits of τ; 360 is the first \"00\" sequence shared with π" 6.)

;;; More efficient shortcuts; most use cases will not require all of the precision available above.
(def e (double e*))
(def π (double π*))
(def τ (double τ*))

;;; Unary functions.
(defn factorial
  ;; FIXME: This is ugly, inefficient, and can't handle vaguely large inputs.
  ;;        Find a way to tail recurse or reduce.
  [x]
  (if (zero? x)
    1
    (*' x (factorial (-' x 1)))))

;;; Binary functions.
(defn gcd
  [x y]
  (if (> x y)
    (recur y x)
    (if (= (rem y x) 0)
      x
      (recur (rem y x) x))))

;;; Variadic functions.
(defn average
  ([x] x)
  ([x y] (/ (+ x y) 2))
  ([x y z] (/ (+ x y z) 3))
  ([x y z & more] (/ (apply + x y z more) (+ 3 (count more)))))

;;; For convenience.
(alias! ! factorial)
(alias! avg average)
(alias! pi π)
(alias! pi* π*)
(alias! tau τ)
(alias! tau* τ*)
